import sys
import requests
import base64

#-------------------------------------------------------------------------------
# Helper function
#------------------------------------------------------------------------------- 
def postCandidate(candidate):
    try:
        r = requests.post(url, json = candidate)
        print(r.status_code)
        if r.status_code not in range(200, 300):
            print(r.text)
        
    except requests.exceptions.RequestException as e:
        print(e)

